package com.learningway.qlearning.Model;import com.learningway.qlearning.Enum.WallType;import lombok.Getter;import lombok.Setter;@Getter@Setterpublic class Square {    private WallType left;    private WallType right;    private WallType top;    private WallType bottom;        public Square() {        this.left = WallType.WALL;        this.right = WallType.WALL;        this.top = WallType.WALL;        this.bottom = WallType.WALL;    }        public void setEntrance(boolean fromUp) {             if(fromUp == true) {            this.top = WallType.ENTRANCE;        } else {            this.left = WallType.ENTRANCE;        }    }        public void setExit(boolean toDown) {        if(toDown == true) {            this.bottom = WallType.EXIT;        } else {            this.right = WallType.EXIT;        }    }        public boolean isExit() {        return this.bottom == WallType.EXIT                || this.right == WallType.EXIT;    }        public boolean isEntrance() {        return this.top == WallType.ENTRANCE                || this.left == WallType.ENTRANCE;    }}